mapper.select:
SELECT us_pwd 
FROM user 
WHERE us_email = #{us_email} and us_name = #{us_name} and us_tel = #{us_tel} and us_status = 1;

mapper.update:
UPDATE user 
SET us_status = 0 
WHERE us_idx = #{us_idx};

UPDATE user 
SET us_name = #{us_name}, us_email = #{us_email}, us_pwd = #{us_pwd}, us_nickname = #{us_nickname}, us_tel = #{us_tel}
WHERE us_idx = #{us_idx};
